$(document).ready(function(){$.get("../controllers/change-language.php",function(a){if(a=="en"){$("#french_flag").css("opacity","0.5")}else{if(a=="fr"){$("#english_flag").css("opacity","0.5")}}});$("#french_flag").on("click",function(a){$("#french_flag").css("opacity","1");var b={language:"fr"};$.post("../controllers/change-language.php",b);$("#english_flag").css("opacity","0.5")});$("#english_flag").on("click",function(a){$("#english_flag").css("opacity","1");var b={language:"en"};$.post("../controllers/change-language.php",b);$("#french_flag").css("opacity","0.5")})});